import React, {useEffect} from 'react';
import './IconBox.css'
import useStore from "../store/main";

const IconBox = (props) => {

    const {OtherUsersIcons, onlineUser} = useStore(state => state)

    function sendRequest() {

        const request = {
            from: onlineUser.username,
            to: OtherUsersIcons.username,
            icon: props.otherIcon
        }

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(request)
        }

        fetch("http://localhost:2002/sendRequest", options)
            .then(res => res.json())
            .then(data => {
                alert(data.message)
            })
    };

    return (
        <>
            {props.otherIcon ? (
                <div className="icon_box">
                    {props.otherIcon}
                    <button onClick={sendRequest}>Ask</button>
                </div>
            ) : <div className="icon_box">{props.icon}</div>
            }
        </>
    );
};

export default IconBox;
