import {create} from "zustand";


const useStore = create((set) => ({

    logPage: false,

    onlineUser: [],

    AllUsers: [],

    MsgReceiver: "",


    changeLogPage: (page) => set({logPage: page}),

    RegisterUser: (value) => set((state) => ({
        onlineUser: value,

        AllUsers: value,
    })),

    LogInUser: (userInfo) => set((state) => ({
        onlineUser: userInfo
    })),

    ChooseReceiver: (value) => set({MsgReceiver: value})

}))

export default useStore;
