import React, {useRef} from 'react';
import UserCard from "../components/UserCard";
import useStore from "../store/main";
import MessageBox from "../components/MessageBox";


const UsersPage = () => {

    const {AllUsers, MsgReceiver, onlineUser, GetAllMessages} = useStore(state => state)
    const messageRef = useRef()



    function sendMessage() {

        const message = {
            from: onlineUser.username,
            to: MsgReceiver,
            text: messageRef.current.value
        }

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(message)
        }

        fetch("http://localhost:2002/sendMessage", options)
            .then(res => res.json())
            .then(data => {
                if (data.error){
                    return alert(data.message)
                }

                alert(data.message)

            })


    }


    return (
        <div className="users_con">
            <div className="users_col">
                {AllUsers.map((user, i) => (
                    <UserCard key={i} user={user} />
                ))}
            </div>
            <div className="sideBar">
                <MessageBox />
                <div className="text_box">
                    <div className="receiver">
                        <p className="sendTo">To:</p>
                        <p className="sendTo">{MsgReceiver}</p>
                    </div>
                    <textarea ref={messageRef}></textarea>
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default UsersPage;
