import React from 'react';
import "./UserCard.css"
import useStore from "../store/main";
import {useNavigate, useParams} from 'react-router-dom'



const UserCard = (props) => {

    const {onlineUser, ChooseReceiver, GetVisitedUserIcons} = useStore(state => state)
    const params = useParams()
    const navigate = useNavigate();


    function visitProfile () {

        const user = {
            username: props.user.username
        }

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(user)
        }

        fetch(`http://localhost:2002/user/${params.username}`, options)
            .then(res => res.json())
            .then(data => {
                GetVisitedUserIcons(data)
                console.log(data)

                navigate(`/users/${params.username}`)
            })
    }

    return (
        <div className="user_card">
            <h4>{props.user.username}</h4>
            <div className="btn_section">
                {onlineUser.username !== props.user.username ? (
                    <>
                        <button onClick={() => ChooseReceiver(props.user.username)}>Send Message</button>
                        <button onClick={visitProfile}>Visit Profile</button>
                    </>

                ) : null}
            </div>
        </div>
    );
};

export default UserCard;
