import {create} from "zustand";


const useStore = create((set) => ({

    logPage: false,

    onlineUser: {
        username: "",
        password: "",
    },

    AllUsers: [],


    changeLogPage: (page) => set({logPage: page}),

    RegisterUser: (value) => set((state) => ({
        onlineUser: value,

        AllUsers: [...state.AllUsers, value],
    })),

    LogInUser: (username, password) => set((state) => ({
        onlineUser: {
            username: username,
            password: password,
        },
    })),

}))

export default useStore;
